import React from 'react';
import { motion } from 'framer-motion';
import './App.css';

function App() {
  return (
    <main className="App">
      <section className="hero">
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="title"
        >
          Iuliia Poliakova
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.8 }}
          className="subtitle"
        >
          I build booking platforms, automate business flows, and design intelligent UI experiences.
        </motion.p>
        <motion.div
          className="buttons"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.8 }}
        >
          <a href="https://voyage-co.com" target="_blank" rel="noreferrer">Visit Website</a>
          <a href="https://voyage-co.com/landing-prepurchase-lite" target="_blank" rel="noreferrer">Landing Page</a>
          <a href="https://github.com/polyulia85/demo_booking" target="_blank" rel="noreferrer">GitHub Demo</a>
          <a href="https://www.linkedin.com/in/iuliiapolialova" target="_blank" rel="noreferrer">LinkedIn</a>
        </motion.div>
      </section>

      <section className="resume-section">
        <motion.div
          className="resume-header"
          initial={{ opacity: 0, x: -30 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.3, duration: 0.8 }}
        >
          <img src="/IMG_20220922_114102.jpg" alt="Iuliia" className="profile-pic" />
          <div className="resume-info">
            <h2>About Me</h2>
            <p>
              Full-Stack Developer & Entrepreneur based in the Bay Area.
              I lead Voyage Co — a tech-driven car rental & inspection company.
            </p>
          </div>
        </motion.div>

        <motion.div
          className="resume-body"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.8 }}
        >
          <h3>Education</h3>
          <p><strong>Economics Degree with Honors</strong> — Russia</p>

          <h3>Skills</h3>
          <ul>
            <li>React, JavaScript, HTML/CSS, PHP, WordPress</li>
            <li>Stripe, HubSpot API, Google Ads, GA4, Tag Manager</li>
            <li>Booking Flows, CRM Automations, UI/UX</li>
          </ul>

          <h3>Experience</h3>
          <p>
            Built and launched multiple booking platforms, ran marketing campaigns, and automated client pipelines
            using Stripe & HubSpot integrations.
          </p>

          <div className="decorative-leaf">
            <img src="/leaf_decor.svg" alt="Leaf Decoration" />
          </div>
        </motion.div>
      </section>
      <img src="/leaf_decor.svg" alt="Decorative Leaf" className="leaf-decor" />

    </main>
  );
}

export default App;
